/* eslint-disable no-irregular-whitespace */
<template>
  <div>
    <!-- 面包屑导航区域 -->
    <el-breadcrumb separator-class="el-icon-arrow-right">
      <el-breadcrumb-item :to="{ path: '/home' }">首页</el-breadcrumb-item>
      <el-breadcrumb-item>图书借读</el-breadcrumb-item>
    </el-breadcrumb>

    <!-- 卡片视图区域 -->
    <el-card class="box-card">

       <!-- 图书列表区 -->
       <el-table :data="booklist" border stripe>
         <el-table-column label="图书ID" prop="book_id"></el-table-column>
         <el-table-column label="图书名称" prop="title"></el-table-column>
         <el-table-column label="出版社" prop="press"></el-table-column>
         <el-table-column label="作者" prop="author"></el-table-column>
         <el-table-column label="简介" prop="outline"></el-table-column>
         <el-table-column label="出版日期" prop="publish_date"></el-table-column>
         <el-table-column label="操作" width="180px">
            <template slot-scope="scope">
              <el-tooltip class="item" effect="dark" content="添加借读信息" placement="top" :enterable="false">
              <el-button type="danger" icon="el-icon-notebook-2" size="mini" @click="lendbook(scope.row)"></el-button>
              </el-tooltip>
           </template>
         </el-table-column>
       </el-table>
        <!-- 分页区域 -->
      <el-pagination
        @size-change="handleSizeChange"
        @current-change="handleCurrentChange"
        :current-page="queryInfo.pagenum"
        :page-sizes="[1,2, 5, 10]"
        :page-size="queryInfo.pagesize"
        layout="total,prev, pager, next, jumper"
        :total="total">
      </el-pagination>
    </el-card>

    <!-- 借读对话框 -->
    <el-dialog
      title="发布借读图书"
      :visible.sync="lenddialogVisible"
      width="50%" @close="lenddialogClosed">
      <!-- 内容主体区域-->
      <el-form :model="lendForm" ref="lendFormRef" label-width="100px" :rules="lendFormRules" >
        <el-form-item label="图书ID" prop="book_id" style="margin-top:-20px">
          <el-input type="textarea" autosize v-model="lendForm.book_id" disabled></el-input>
        </el-form-item>
        <el-form-item label="图书名称" prop="name">
          <el-input type="textarea" autosize v-model="lendForm.name" :disabled="isdisabled"></el-input>
        </el-form-item>
        <el-form-item label="联系方式" prop="mobile">
          <el-input type="textarea" autosize v-model="lendForm.mobile"></el-input>
        </el-form-item>
      </el-form>
      <el-form style="text-align:right">
        <el-select v-model="lendForm.days" placeholder="选择借读天数" style="margin-right: 3%">
            <el-option
              v-for="item in options"
              :key="item.value"
              :label="item.label"
              :value="item.value">
            </el-option>
         </el-select>
          <el-button style="margin-top:-70px" type="primary" @click="confirmlend()">确 定</el-button>
      </el-form>
    </el-dialog>
  </div>
</template>

<script>
export default {
  data () {
    return {
      // 获取用户列表的参数对象
      queryInfo: {
        // 当前页数
        pagenum: 1,
        query: ''
      },
      options: [{
        value: '1',
        label: '1天'
      }, {
        value: '2',
        label: '2天'
      }, {
        value: '3',
        label: '3天'
      }, {
        value: '4',
        label: '4天'
      }, {
        value: '5',
        label: '5天'
      }, {
        value: '6',
        label: '6天'
      }, {
        value: '7',
        label: '7天'
      }],
      booklist: [],
      total: 0,
      lenddialogVisible: false,
      isdisabled: true,

      lendForm: {
        book_id: '',
        name: '',
        mobile: '',
        days: ''
      },
      lendFormRules: {
        days: [
          { required: true, message: '请选择借读天数', trigger: 'blur' }
        ],
        phone: [
          { required: true, message: '请填写联系方式', trigger: 'blur' }
        ]
      }
    }
  },
  created () {
    this.getBookList()
  },
  methods: {

    // 展示借读对话框
    lendbook (book) {
      this.lendForm.book_id = book.book_id
      this.lendForm.name = book.title
      this.lenddialogVisible = true
    },
    // 确定借读
    async confirmlend () {
      const { data: res } = await this.axios.post('/lendbook', {
        user_email: '17301115@bjtu.edu.cn',
        book_id: this.lendForm.book_id,
        book_name: this.lendForm.name,
        lend_time: this.lendForm.days,
        telephone: this.lendForm.mobile
      }
      )
      console.log(res)
      if (res.status !== '1') return this.$message.error('添加借读信息失败')
      this.$message.success('添加借读信息成功')
    },
    // 获取图书列表
    async getBookList () {
      // 获取的请求
      console.log(this.queryInfo.pagesize)
      const { data: res } = await this.axios.post('/selectBookAll', {
        page: this.queryInfo.pagenum
      })
      console.log(res)
      if (res.status_code !== '1') return this.$message.error('获取图书列表失败')
      this.$message.success('获取图书列表成功')
      this.booklist = res.data
      this.total = parseInt(res.sum)
      this.booklist.forEach((item, i) => {
        if (res.data[i].is_shelf === 1) {
          res.data[i].is_shelf = '上架'
        } else {
          res.data[i].is_shelf = '下架'
        }
      })
    },
    // 模糊查询图书
    async selectBookQuery () {
      // 获取的请求
      console.log(this.queryInfo.pagesize)
      const { data: res } = await this.axios.post('/selectBookQuery', {
        store_id: this.queryInfo.store_id,
        // store_id: 1,
        pagenum: this.queryInfo.pagenum,
        pagesize: this.queryInfo.pagesize,
        query: this.queryInfo.query
      })
      console.log(res)
      if (res.status !== '1') return this.$message.error('模糊获取图书列表失败')
      this.$message.success('模糊获取图书列表成功')
      this.booklist = res.data
      this.total = parseInt(res.sum)
      this.booklist.forEach((item, i) => {
        console.log(res.data[i].author)
        console.log(res.data[i].is_shelf)
        if (res.data[i].is_shelf === 1) {
          res.data[i].is_shelf = '上架'
        } else {
          res.data[i].is_shelf = '下架'
        }
      })
    },
    // 监听页码值改变的事件
    handleCurrentChange (newPage) {
      console.log(newPage)
      this.queryInfo.pagenum = newPage
      this.getBookList()
    },
    lenddialogClosed () {
      this.$refs.lendFormRef.resetFields()
    }
  }
}
</script>

<style lang="less" scoped>
.editbtn{
  float: right;
  line-height: 15px;
}
</style>
